import logo from './logo.svg';
import './Styles.css';
import { css } from '@emotion/styled'
import styled from '@emotion/styled'
import { useForm } from "react-hook-form"
import * as yup from 'yup'
import {yupResolver} from '@hookform/resolvers/yup'

const Input = styled.input`
border-radius: 2px;
text-indent: 5px;
border: 1px #717274 solid;
outline: none;
height: 35px;
transition: all 0.3s ease;
&:focus {
  box-shadow: 0 0 10px #0077C5;
}
`

const Label = styled.label`
display: flex;
height: 1.5em;
`
const Button = styled.button`
height: 4em;
border-radius: 2px;
cursor: pointer;
border: 1px #0077C5 solid;
background-color: #0077C5;
color: white;
transition: all 0.3s ease;
&:hover {
  transform: scale(1.01);
}
`

const inputform = ({ label, register }) => {

  return (

    <div className='field'>
      <Label >{label}</Label>
      <Input {...register(label)} type="text" />
    </div>

  )

}

function App() {

  const validationSchema = yup.object({
    name: yup.string().required('Missing Name'),
    surname: yup.string().required('Missing Surname'),
    email: yup.string().required('Missing Email').email('Invalid email format'),
    password: yup.string().required('Missing Password'),
    dateofbirth: yup.date().required('Missing Date of Birth'),

  }).required()

  const { register, handleSubmit } = useForm({
    resolver: yupResolver(validationSchema),
    defaultValues: {
      name: '',
      surname: '',
      email: '',
      password: '',
      dateofbirth: ''
    }
  })
  const onSubmit = data => {
    console.log('data', data);
  }

  return (

    <div>
      <div className='form-container'>

        <div className='form-header'>
          <h1>Create an Account</h1>
          <h3>Please fill in the neccesarry details to proceed.</h3>
        </div>

        <form className="container" onSubmit={handleSubmit(onSubmit)}>

          {/* <inputform label='Name' register={register} /> */}

          <div className="field">
            <Label for="name">Name</Label>
            <Input {...register('name')} type="text" />
          </div>
          <div className="field">
            <Label for="surname">Surname</Label>
            <Input {...register('surname')} type="text" />
          </div>
          <div className="field">
            <Label for="email">Email</Label>
            <Input {...register('email')} type="email" />
          </div>
          <div className="field">
            <Label for="password">Password</Label>
            <Input {...register('password')} type="password" />
          </div>
          <div className="field">
            <Label for="dob">Date of Birth</Label>
            <Input {...register('dateofbirth')} type="date" />
          </div>

          <Button>Create Account</Button>

        </form>

        <div className='form-footer'>
          <p>some footer shit for a signup page</p>
        </div>


      </div>
    </div>




  );
}

export default App;
