import React from 'react'
import styled from '@emotion/styled'
import { useForm } from "react-hook-form"
import * as yup from 'yup'
import { yupResolver } from '@hookform/resolvers/yup'
import '../index.css';

type UserSubmitForm = {
    name: string;
    email: string;
    password: string;
};


export const SignUpForm = () => {

    const validationSchema = yup.object({
        name: yup.string().required('Missing Name'),
        surname: yup.string().required('Missing Surname'),
        email: yup.string().required('Missing Email').email('Invalid email format'),
        password: yup.string().required('Missing Password'),
        dateofbirth: yup.date().required('Missing Date of Birth'),

    }).required()

    const { register, handleSubmit, formState: { errors } } = useForm({
        resolver: yupResolver(validationSchema),
        defaultValues: {
            name: '',
            email: '',
            password: '',
        }
    })
    console.log(errors);
    const onSubmit = (data: UserSubmitForm) => {
        console.log('data', data);
    }







    return (
        <div className='form-container'>
            <form className='form-fields'>

                <h1>Sign Up</h1>
                <p style={{ marginBottom: '20px' }}>Let's get you started</p>

                <div className="field">
                    <StyledInput type="text" placeholder="Name" />
                </div>
                <div className="field">
                    <StyledInput type="text" placeholder="Email" />
                </div>
                <div className="field">
                    <StyledInput type="password" placeholder="Password" />
                </div>

                <StyledButton>Sign Up</StyledButton>

                <p>Already have an account? <a style={{ color: '#1B82E9 ' }}>Log In</a></p>

                <p style={{ fontSize: '0.8rem', margin: '130px 100px 0px 100px', textAlign: 'center' }}>By signing up to create an account I accept Company's <a style={{ color: '#1B82E9 ' }}>Terms of Use and Privacy Policy.</a></p>
            </form>


        </div>
    )
}

const StyledInput = styled.input`
width: 335px;
border-radius: 7px;
text-indent: 10px;
border: 2px #E4E5ED solid;
outline: none;
height: 40px; 
transition: all 0.3s ease;
&:focus {
    border: 2px #59C3F4 solid;
}

`

const StyledButton = styled.button`
width: 335px;
height: 40px;
border-radius: 7px;
cursor: pointer;
border: 1px #344563 solid;
background-color: #344563;
color: white;
transition: all 0.3s ease;
&:hover {
  transform: scale(1.01);
}
`
