import React from 'react'
import styled from '@emotion/styled'
import { useForm } from "react-hook-form"
import * as yup from 'yup'
import { yupResolver } from '@hookform/resolvers/yup'
import '../index.css';


export const SignUpForm = () => {
    return (
        <div className='form-container'>
            <div className='form-fields'>
                
                <h1>Sign Up</h1>
                <p>Let's get you started</p>
            
                <div className="field">
                    <StyledInput type="text" placeholder="Name" />
                </div>
                <div className="field">
                    <StyledInput type="text" placeholder="Email" />
                </div>
                <div className="field">
                    <StyledInput type="text" placeholder="Password" />
                </div>
            
                <StyledButton>Sign Up</StyledButton>
            
                <p>Already have an account? <a style={{color:'#1B82E9 '}}>Log In</a></p>

                <p style={{fontSize:'0.8rem', margin:'150px 50px 5px 5px'}}>By signing up to create an account I accept Company's <a style={{color:'#1B82E9 '}}>Terms of Use and Privacy Policy</a></p>
            </div>

            
        </div>     
    )
}
 
const StyledInput = styled.input`
width: 335px;
border-radius: 7px;
text-indent: 10px;
border: 2px #E4E5ED solid;
outline: none;
height: 40px; 
transition: all 0.3s ease;
&:focus {
    border: 2px #59C3F4 solid;
}

`

const StyledButton = styled.button`
width: 335px;
height: 40px;
border-radius: 7px;
cursor: pointer;
border: 1px #344563 solid;
background-color: #344563;
color: white;
transition: all 0.3s ease;
&:hover {
  transform: scale(1.01);
}
`
